<?php  function ChangeToSlug($text){        //Đổi chữ hoa thành chữ thường        // $slug = strtolower($text);        // dd($text);        //Đổi ký tự có dấu thành không dấu        $slug = $text;        $a = ['á', 'à', 'ả', 'ạ', 'ã', 'ă', 'ắ', 'ằ', 'ẳ', 'ẵ', 'ặ', 'â', 'ấ', 'ầ', 'ẩ', 'ẫ', 'ậ', 'Ấ', 'Á', 'À', 'Ả', 'Ạ', 'Ã', 'Ă', 'Ắ', 'Ằ', 'Ẳ', 'Ẵ', 'Ặ', 'Â', 'Ầ', 'Ẩ', 'Ẫ', 'Ậ'];        $slug = str_replace($a, 'a', $slug);        $e = ['é', 'è', 'ẻ', 'ẽ', 'ẹ', 'ê', 'ế', 'ề', 'ể', 'ễ', 'ệ', 'É', 'È', 'Ẻ', 'Ẽ', 'Ẹ', 'Ê', 'Ế', 'Ề', 'Ể', 'Ễ', 'Ệ'];        $slug = str_replace($e, 'e', $slug);        $i = ['i', 'í', 'ì', 'ỉ', 'ĩ', 'ị', 'I', 'Í', 'Ì', 'Ỉ', 'Ĩ', 'Ị'];        $slug = str_replace($i, 'i', $slug);        $o = ['ó', 'ò', 'ỏ', 'õ', 'ọ', 'ô', 'ố', 'ồ', 'ổ', 'ỗ', 'ộ', 'ơ', 'ớ', 'ờ', 'ở', 'ỡ', 'ợ', 'Ó', 'Ò', 'Ỏ', 'Õ', 'Ọ', 'Ô', 'Ố', 'Ồ', 'Ổ', 'Ỗ', 'Ộ', 'Ơ', 'Ớ', 'Ờ', 'Ở', 'Ỡ', 'Ợ'];        $slug = str_replace($o, 'o', $slug);        $u = ['ú', 'ù', 'ủ', 'ũ', 'ụ', 'ư', 'ứ', 'ừ', 'ử', 'ữ', 'ự', 'Ú', 'Ù', 'Ủ', 'Ũ', 'Ụ', 'Ư', 'Ứ', 'Ừ', 'Ử', 'Ữ', 'Ự'];        $slug = str_replace($u, 'u', $slug);        $y = ['ý', 'ỳ', 'ỷ', 'ỹ', 'ỵ', 'Ý', 'Ỳ', 'Ỷ', 'Ỹ', 'Ỵ'];        $slug = str_replace($y, 'y', $slug);        $d = ['Đ', 'đ'];        $slug = str_replace($d, 'd', $slug);        //Xóa các ký tự đặt biệt        $kt = ['/', '|', '@', '#', '&', '*', ';', '^', '$', '%', '`', '(', ')', '.', ',', '\\', ' ', '£', 'ä', 'º', '´'];        $slug = str_replace($kt, '-', $slug);        $slug = str_replace('---', '-', $slug);        $slug = strtolower($slug);        //Đổi nhiều ký tự gạch ngang liên tiếp thành 1 ký tự gạch ngang        //Phòng trường hợp người nhập vào quá nhiều ký tự trắng        return $slug;        }?>